import BigNumber from 'bignumber.js';
export declare enum Denomination {
    /**
     * values for token amounts in base units (no decimal)
     */
    BASE = "BASE",
    /**
     * values of token amounts (w/ decimal)
     */
    TOKEN = "TOKEN"
}
declare type Amount<T> = {
    type: T;
    amount: () => BigNumber;
};
export declare type BaseAmount = Amount<Denomination.BASE>;
export declare type TokenAmount = Amount<Denomination.TOKEN>;
export declare type Amounts = TokenAmount | BaseAmount;
export {};
